import { jsTPS_Transaction } from '../common/jsTPS';

export default class AddNewItem_Transaction extends jsTPS_Transaction {
    constructor(initApp) {
        super()
        this.app = initApp;
        this.id;
    }
    doTransaction() {
        console.log("Crikey");
        console.log("ran??");
        let newItem = this.app.makeNewToDoListItem();
        this.app.state.currentList.items.push(newItem);
        this.app.setState({
            currentList: this.app.state.currentList,
            nextListItemId: this.App.state.nextListItemId+1
        })
    }
    undoTransaction() {
        console.log("mikey");
    }
}
